    oncommand() {
        let text = "";
        for (const t of gBrowser.tabs) {
            let doc = t.linkedBrowser.contentDocument;
            if (doc) {
                text = `${text}${w.title}\n${doc.location.href}\n`;
            } else {
                const w = t.linkedBrowser;
                text = `${text}${w.contentTitle}\n${doc.currentURI.spec}\n`;
            }
        }
        addMenu.copy(text);
    }
