new function () {
    var CatGroup = IdentGroup({
        class: 'showFirstText',
    });
    CatGroup([{
        label: _IS_HZ ? "其他浏览器中打开" : "Open in other browser",
        id: 'identity-contextmenu-openwithbrowser',
        accesskey: 'e',
        onclick({target}) {
            let browser = addMenu.prefs.getStringPref("chooseBrowser", "");
            function isFileExists(path) {
                if (!path) return false;
                let app = Cc["@mozilla.org/file/local;1"].createInstance(Ci.nsIFile);
                app.initWithPath(path);
                return app.exists();
            }

            let href = addMenu.convertText("%RLINK_OR_URL%");
            if (!isFileExists(browser)) {
                target.nextSibling.click();
                return;
            }
            if (href.startsWith('http')) {
                addMenu.exec(browser, href);
            } else {
                addMenu.exec(browser);
            }
        },
        image: 'chrome://devtools/skin/images/browsers/edge.svg'
    }, {
        id: 'identity-contextmenu-openwithbrowser-changebrowser',
        label: _IS_HZ ? "更换浏览器" : "Change browser",
        tooltiptext: _IS_HZ ? "更换浏览器" : "Change browser",
        style: 'list-style-image: url("chrome://global/skin/icons/settings.svg");',
        oncommand(event) {
            const {prefs} = addMenu;
            function chooseBrowser() {
                let fp = Cc['@mozilla.org/filepicker;1'].createInstance(Ci.nsIFilePicker);
                fp.init(window, _IS_HZ ? "设置浏览器路径" : "Set browser path", Ci.nsIFilePicker.modeOpen);
                fp.appendFilter(_IS_HZ ? "执行文件" : "Executable file", "*.exe"); // 非 Windows 必须注释这一行，不过不一定能用
                fp.open(res => {
                    if (res != Ci.nsIFilePicker.returnOK) return;
                    prefs.setStringPref("chooseBrowser", fp.file.path);
                });
            }

            alert(_IS_HZ ? "请先设置浏览器的路径!!!" : "Please set browser path first!!!");
            chooseBrowser();
        }
    }])
};
