new function () {
    var groupMenu = new GroupMenu({
        class: 'showFirstText',
        condition: 'select',
        insertAfter: 'context-copy',
        onshowing(item) {
            $("context-print-selection-new")
                .setAttribute("tooltiptext", $("context-print-selection-new").label);
            $("context-viewpartialsource-selection-new")
                .setAttribute("tooltiptext", $("context-viewpartialsource-selection-new").label);
            item.removeAttribute("onshowing");
        },
    });
    groupMenu([{
        'data-l10n-href': 'textActions.ftl',
        'data-l10n-id': 'text-action-copy',
        tooltiptext: _IS_HZ ? '左键：复制\nShift + 左键：复制存文本' : 'Left click: copy content\nShift + Left click: copy plain text',
        class: 'copy',
        oncommand({button, shiftKey}) {
            if (button === 0) {
                if (shiftKey)
                    addMenu.copy(addMenu.convertText("%s"));
                else
                    document.getElementById('cmd_copy').doCommand();
            }
        }
    }, {
        label: _IS_HZ ? '保存文本' : 'Save slected text quickly',
        tooltiptext: _IS_HZ ? '左键：快速保存选定文本\n中键：快速保存选定文本并打开' : 'Left click: save slected text quickly\nMiddle click: save selected text quickly and open it',
        style: 'list-style-image: url(data:image/svg+xml;base64,)',
        onclick(event) {
            const {FileUtils, NetUtil} = globalThis;
            const data = addMenu.convertText("%SEL%");

            const fp = Cc['@mozilla.org/filepicker;1'].createInstance(Ci.nsIFilePicker);
            fp.init(window, _IS_HZ ? "另存为" : "Save as", Ci.nsIFilePicker.modeSave);
            fp.appendFilter(_IS_HZ ? "文本文件" : "Text file", "*.txt");
            fp.defaultString = addMenu.convertText("%TITLES%") + '.txt';
            fp.open(res => {
                if (res != Ci.nsIFilePicker.returnOK) return;
                const aFile = fp.file;
                const ostream = FileUtils.openSafeFileOutputStream(aFile);
                const converter = Cc["@mozilla.org/intl/scriptableunicodeconverter"].createInstance(Ci.nsIScriptableUnicodeConverter);
                converter.charset = "gbk";
                const istream = converter.convertToInputStream(data);
                NetUtil.asyncCopy(istream, ostream, function(status) {
                    if (!Components.isSuccessCode(status)) {
                        // Handle error!
                        return;
                    }
                    if (event.button == 1) aFile.launch();
                });
            });
        }
    }, {
        id: 'context-print-selection-new',
        command: 'context-print-selection',
        'data-l10n-href': 'browserContext.ftl',
        'data-l10n-id': 'main-context-menu-print-selection',
        style: 'list-style-image: url(data:image/svg+xml;base64,)',
    }, {
        id: 'context-viewpartialsource-selection-new',
        'data-l10n-href': 'browserContext.ftl',
        'data-l10n-id': "main-context-menu-view-selection-source",
        command: 'context-viewpartialsource-selection',
        style: 'list-style-image: url(data:image/svg+xml;base64,)'
    }]);
    css("#context-copy, #context-print-selection, #context-viewpartialsource-selection, #context-sep-selectall { visibility: collapse }");
}
