new function () {
    var items = [{
        id: 'addMenu-sitesearch-insertpoint',
        label: 'separator',
    }, {
        label: _IS_HZ ? '生成二维码' : 'Generate QR code',
        where: 'tab',
        url: "https://my.tv.sohu.com/user/a/wvideo/getQRCode.do?text=%s",
        image: "data:image/svg+xml;base64,"
    }];
    var menu = PageMenu({
        id: 'addMenu-search-select',
        condition: 'select',
        image: "data:image/svg+xml;base64,",
        accesskey: 'S',
        insertBefore: 'context-searchselect',
        onshowinglabel: _IS_HZ ? "搜索: %SEL%" : "Search %SEL% by",
        onshowing(item) {
            const searchConfigFile = Services.dirsvc.get('UChrm', Ci.nsIFile),
            popupNode = item.querySelector('menupopup');

            searchConfigFile.append("UserConfig"); // 配置文件存放在 UserConfig 目录下
            if (searchConfigFile.exists()) {
                searchConfigFile.append("_allsearch.json"); // 配置文件名为 _allsearch.json
            }
            popupNode.querySelectorAll('.auto-generated').forEach(m => m.remove());
            let ins = popupNode.firstChild;
            if (searchConfigFile.exists()) {
                let searchJson = false;
                try {
                    searchJson = JSON.parse(loadText(searchConfigFile));
                } catch (e) {
                    return addMenu.error('_allsearch.json文件不存在或 JSON 解析出错！\n', e);
                }
                searchJson?.forEach(g => {
                    if (Array.isArray(g.list) && g.list.length) {
                        let obj = {
                            label: g.nameZh || g.name,
                            class: "auto-generated search-menu showFirstText",
                            hidden: g.data.visible === false,
                            onclick(event) {
                                if (event.target != event.currentTarget) return;
                                var firstItem = event.currentTarget.querySelector('menuitem');
                                if (!firstItem) return;
                                if (event.button === 1) {
                                    checkForMiddleClick(firstItem, event);
                                } else {
                                    firstItem.doCommand();
                                    closeMenus(event.currentTarget);
                                }
                            }
                        };

                        let items;
                        obj._items = items = g.list.map(e => {
                            return {
                                label: e.nameZh || e.name,
                                where: 'tab',
                                url: e.url,
                                hidden: e.data.visible === false
                            }
                        });

                        // 显示为横排菜单，如果想显示成三级菜单，注释下面这三句
                        // obj._group = true;
                        // obj.class = ((obj.class || "") + " search-menu-group").trim();
                        // items.unshift({
                        //     label: g.nameZh || g.name,
                        //     hidden: g.data.visible === false,
                        //     onclick(event) {
                        //         if (event.target != event.currentTarget) return;
                        //         let nextSib = event.target.nextSibling;
                        //         while (nextSib) {
                        //             if (nextSib.hidden === false)
                        //                 nextSib.click(event);
                        //             nextSib = nextSib.nextSibling;
                        //         }
                        //     },
                        // })

                        ins.before(addMenu.newMenu(obj, {
                            isTopMenuitem: true,
                            insertPoint: ins
                        }, document));
                    }
                });
            } else {
                Services.search.getVisibleEngines().then(engines => {
                    for (const item of engines) {
                        const alias = item._definedAliases[0] || item._metaData.alias;
                        if (!alias) continue;
                        let menuitem = addMenu.newMenuitem({
                            label: item._name,
                            class: "auto-generated",
                            where: 'tab',
                            text: "%s",
                            keyword: alias
                        }, {
                            isTopMenuitem: true,
                            insertPoint: ins
                        }, document);
                        ins.before(menuitem);
                    }
                });
            }
        }
    });
    menu(items);
    css("#context-searchselect { display: none } #contentAreaContextMenu #addMenu-search-select .menu-accel-container { visibility: hidden; }");
    function loadText(aFile) {
        var fstream = Cc["@mozilla.org/network/file-input-stream;1"].createInstance(Ci.nsIFileInputStream);
        var sstream = Cc["@mozilla.org/scriptableinputstream;1"].createInstance(Ci.nsIScriptableInputStream);
        fstream.init(aFile, -1, 0, 0);
        sstream.init(fstream);

        var data = sstream.read(sstream.available());
        try {
            data = decodeURIComponent(escape(data));
        } catch (e) { }
        sstream.close();
        fstream.close();
        return data;
    }
};
