//隐藏相同项。必须，不能删除
function syncHidden(event) {
    for (const elem of event.target.children) {
        const raw = $(elem.getAttribute('command'));
        if (!raw) {
            elem.hidden = true;
            return;
        }
        elem.hidden = raw.hidden;
        elem.collapsed = raw.collapsed;
        elem.disabled = raw.disabled;
    }
};